package com.github.elbean.ignite.cache.api.config;

import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;
import org.springframework.web.servlet.config.annotation.ViewControllerRegistry;
import org.springframework.web.servlet.config.annotation.WebMvcConfigurerAdapter;

/**
 * Configure the /docs endpoint
 * @author BStilson
 */
@Configuration
public class DocsEndpointConfig {

    @Bean
    public WebMvcConfigurerAdapter forwardToIndex() {
        return new WebMvcConfigurerAdapter() {
            @Override
            public void addViewControllers(ViewControllerRegistry registry) {
                // forward requests to /docs the appropriate index.html
                registry.addRedirectViewController("/docs", "/docs/index.html");
            }
        };
    }

}
